mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.new_money as newMoney ,yuangong.yuangong_name as yuangongName ,yuangong.yuangong_phone as yuangongPhone ,yuangong.yuangong_id_number as yuangongIdNumber ,yuangong.yuangong_photo as yuangongPhoto
FROM baoxiu a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN yuangong yuangong ON a.yuangong_id = yuangong.id
and a.id in
#{item}
and a.yonghu_id = #{params.yonghuId}
and a.yuangong_id = #{params.yuangongId}
and a.baoxiu_name like CONCAT('%',#{params.baoxiuName},'%')
and a.baoxiu_types = #{params.baoxiuTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.baoxiu_content like CONCAT('%',#{params.baoxiuContent},'%')
and a.baoxiu_weixiu_types = #{params.baoxiuWeixiuTypes}
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and yuangong.yuangong_name like CONCAT('%',#{params.yuangongName},'%')
and yuangong.yuangong_phone like CONCAT('%',#{params.yuangongPhone},'%')
and yuangong.yuangong_id_number like CONCAT('%',#{params.yuangongIdNumber},'%')
order by a.${params.orderBy} desc
