mapper.select:
SELECT
-- 级联表的字段 ,fangwu.fangwu_name as fangwuName ,fangwu.fangwu_address as fangwuAddress ,fangwu.fangwu_photo as fangwuPhoto ,fangwu.fangwu_types as fangwuTypes ,fangwu.fangwu_delete as fangwuDelete ,fangwu.fangwu_content as fangwuContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.new_money as newMoney
FROM fangwu_collection a left JOIN fangwu fangwu ON a.fangwu_id = fangwu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.fangwu_id = #{params.fangwuId}
and a.yonghu_id = #{params.yonghuId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and fangwu.fangwu_name like CONCAT('%',#{params.fangwuName},'%')
and fangwu.fangwu_address like CONCAT('%',#{params.fangwuAddress},'%')
and fangwu.fangwu_types = #{params.fangwuTypes}
and fangwu.fangwu_delete >= #{params.fangwuDeleteStart}
and fangwu.fangwu_delete <= #{params.fangwuDeleteEnd}
and fangwu.fangwu_delete = #{params.fangwuDelete}
and fangwu.fangwu_content like CONCAT('%',#{params.fangwuContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
order by a.${params.orderBy} desc
